INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-幻惑の光', '基本1', 'エンジェルハィロゥ', '一般', '幻惑の光', 'ケンワクノヒカリ', '1', 1, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '2', '—', NULL, '光の信号によって、運動能力を低下させる催眠術をかけるエフェクト。', '射撃攻撃を行なう。命中した場合、対象にバッドステータスの放心を与える。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-光芒の疾走', '基本1', 'エンジェルハィロゥ', '一般', '光芒の疾走', 'コウホウノシッソウ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, '弱い光によって目くらましをかけ、その隙に移動するエフェクト。', 'あなたは戦闘移動を行なう。この移動では、離脱を行なえる。また、移動中に他のエンゲージに接触しても移動を終える必要はなく、封鎖の影響も受けない。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-主の恩恵', '基本1', 'エンジェルハィロゥ', '一般', '主の恩恵', 'シュノオンケイ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '体内に光を集め、次に発生させる光を増幅するエフェクト。', 'そのメインプロセスの間、あなたが行なうエンジェルハィロゥのエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-主の右腕', '基本1', 'エンジェルハィロゥ', '一般', '主の右腕', 'シュノミキウテ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '相手のもっとも弱い部分を見抜き、正確に攻撃するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-水晶の眼', '基本1', 'エンジェルハィロゥ', '一般', '水晶の眼', 'スイショウノメ', '3', 3, 'メジャーアクション/リアクション', '〈知覚〉', '—', '—', '—', '2', '—', NULL, '遠くの風景を蜃気楼のように眼前に投影するエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-スターダストレイン', '基本1', 'エンジェルハィロゥ', '一般', 'スターダストレイン', 'スタータストレイン', '3', 3, 'メジャーアクション', '〈RC〉', '対決', 'シーン(選択)', '視界', '6', '—', NULL, 'レーザー光線を乱射するエフェクト。', 'このエフェクトを組み合わせた射撃攻撃の対象をシーン(選択)に、射程を視界に変更する。ただし、その攻撃の攻撃力を−[20−LV×5](最大0)する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-絶対の孤独', '基本1', 'エンジェルハィロゥ', '一般', '絶対の孤独', 'セッタイノコトク', '3', 3, 'メジャーアクション', '〈RC〉', '対決', '単体', '視界', '3', '—', NULL, '光を遮断して闇を作り、行動を阻害するエフェクト。', '射撃攻撃を行なう。命中した場合、そのラウンドの間、対象が行なうあらゆる判定のダイスを−[LV+1]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-小さな塵', '基本1', 'エンジェルハィロゥ', '一般', '小さな塵', 'チイサナチリ', '5', 5, 'メジャーアクション', '〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '精密射撃を行なえるように、知覚能力を強化するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-光の弓', '基本1', 'エンジェルハィロゥ', '一般', '光の弓', 'ヒカリノユミ', '10', 10, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '1', '—', NULL, '光を一点に収束し、解き放つことで熱衝撃波を発生させるエフェクト。', '「攻撃力:+[LV+2]」の射撃攻撃を行なう。この攻撃では、あなたと同じエンゲージにいるキャラクターを対象にできない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ピンポイントレーザー', '基本1', 'エンジェルハィロゥ', '一般', 'ピンポイントレーザー', 'ヒンホイントレーサー', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '装甲の隙間を見抜き、撃ち抜くエフェクト。', 'このエフェクトを組み合わせた攻撃では、対象の装甲値を無視してダメージを算出する。ただし、その攻撃の攻撃力を−[5−LV](最大0)すること。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-光の衣', '基本1', 'エンジェルハィロゥ', '一般', '光の衣', 'ヒカリノコロモ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '4', '80%', NULL, '光の屈折率を変化させて、銃弾やみずからの姿などを隠すエフェクト。', 'そのメインプロセスの間、あなたが行なうメジャーアクションに対するリアクションは、クリティカル値が+1される。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-マスヴィジョン', '基本1', 'エンジェルハィロゥ', '一般', 'マスヴィジョン', 'マスウィション', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '4', '100%', NULL, '残像を複数作り出し、一斉攻撃を行なうエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×5]する、このエフェクトは1シナリオに3回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-光の守護', '基本1', 'エンジェルハィロゥ', '一般', '光の守護', 'ヒカリノシュコ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, '光を集め、攻撃を受ける直前に残像と入れ替わるエフェクト。', 'あなたにHPダメージを適用する直前に使用する。そのHPダメージを0にする。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-光の裁き', '基本1', 'エンジェルハィロゥ', '一般', '光の裁き', 'ヒカリノサハキ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '単体', '—', '5', 'ピュアブリード', NULL, '周囲を埋め尽くす光をもって、敵に回避不可能の一撃を撃つエフェクト。', 'このエフェクトを組み合わせた射撃攻撃は判定のダイスを+[LV×2]個し、また攻撃のリアクションでドッジを選択することができない。このエフェクトは1シナリオに1回まで使用でき、攻撃の対象は単体でなければならない。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-因果歪曲', '基本1', 'バロール', '一般', '因果歪曲', 'インカワイキョク', '3', 3, 'メジャーアクション', 'シンドローム', '—', '範囲(選択)', '—', '3', '—', NULL, '物理法則をねじ曲げて、効果を拡大するエフェクト。', 'このエフェクトを組み合わせたエフェクトの対象を範囲(選択)に変更する。ただし、この効果を受けたエフェクトでは、あなたと同じエンゲージにいるキャラクターを対象にできない。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-巨人の斧', '基本1', 'バロール', '一般', '巨人の斧', 'キョシンノオノ', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '3', '—', NULL, '重力を操作することで、武器の見かけの重量を増大させ、攻撃力を増すエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×3]する。ただし、このエフェクトを組み合わせた攻撃の判定のダイスを−2個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-黒の鉄槌', '基本1', 'バロール', '一般', '黒の鉄槌', 'クロノテッツイ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '1', '—', NULL, '局所的に重力を発生させ、敵を押し潰すエフェクト。犠牲者の身体は見るも無惨に圧壊する。', '「攻撃力:+[LV×2+2]」の射撃攻撃を行なう。この攻撃では、あなたと同じエンゲージにいるキャラクターを対象にできない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-孤独の魔眼', '基本1', 'バロール', '一般', '孤独の魔眼', 'コトクノマカン', '3', 3, 'オートアクション', '—', '自動成功', '効果参照', '視界', '4', '—', NULL, '重力を制御し、攻撃を自分に引き寄せるエフェクト。', 'あなたを対象に含む「対象:範囲」または「対象:範囲(選択)」の攻撃の判定が行なわれる直前に使用する。その攻撃の対象をあなたひとりに変更する。ただし、その攻撃であなたはカバーリングの対象にならない。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-漆黒の拳', '基本1', 'バロール', '一般', '漆黒の拳', 'シッコクノコフシ', '10', 10, 'メジャーアクション', '〈白兵〉', '対決', '単体', '武器', '3', '—', NULL, '重力の塊を、自分の手で直接敵にぶつけるエフェクト。いかなる装甲も、暗黒の重力塊には無力だ。', '素手による白兵攻撃を行なう。このエフェクトを組み合わせた攻撃の攻撃力を+LVし、さらに対象の装甲値を無視してダメージを算出する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-斥力障壁', '基本1', 'バロール', '一般', '斥力障壁', 'セキリョクショウヘキ', '5', 5, 'オートアクション', '—', '自動成功', '単体', '視界', '2', '—', NULL, '対象の周囲を斥力場で覆い、攻撃から守るエフェクト。', '対象にHPダメージが適用される直前に使用する。対象が受ける(予定)ダメージを−[1D+LV×2]する。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-斥力跳躍', '基本1', 'バロール', '一般', '斥力跳躍', 'セキリョクチョウヤク', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, '斥力を瞬間的に発生させ、その反発力でジャンプするエフェクト。', '飛行状態で戦闘移動を行なう。その際、移動距離を+[LV×5]mする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ダークマター', '基本1', 'バロール', '一般', 'ダークマター', 'タークマター', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '“魔眼”に意識を集中し、力を操るための準備をするエフェクト。', 'そのメインプロセスの間、バロールのエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-灰色の庭', '基本1', 'バロール', '一般', '灰色の庭', 'ハイイロノニワ', '3', 3, 'セットアッププロセス', '—', '自動成功', '単体', '視界', '2', '—', NULL, '敵の“重さ”を増加させ、素早さを下げるエフェクト。重力と事象を支配するあなたの前では、誰であれその実力を発揮することはできない。', 'そのラウンドの間、対象の【行動値】を−[LV×3]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-魔王の理', '基本1', 'バロール', '一般', '魔王の理', 'マオウノコトワリ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '重力のコントロールに長けていることを表わすエフェクト。あなたの力は空間を歪め、宇宙の法則さえもかき乱す。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-冥界の檻', '基本1', 'バロール', '一般', '冥界の檻', 'メイカイノオリ', '5', 5, 'オートアクション', '〈RC〉', '対決', '単体', '視界', '5', '—', NULL, '敵に高重力をかけて、その移動を止めるエフェクト。', '対象が戦闘移動、全力移動を宣言した際に宣言する。対象に射撃攻撃を行なうこと。命中した場合、対象の移動を失敗させる。この判定に、あなたはダイスを+LV個する。このエフェクトはカバーリングされることはなく、1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-時間凍結', '基本1', 'バロール', '一般', '時間凍結', 'シカントウケツ', '1', 1, 'イニシアチブプロセス', '—', '自動成功', '自身', '至近', '5', '80%', NULL, '時間凍結領域を作り出すエフェクト。', 'あなたはイニシアチブプロセスでメインプロセスを行なえる。また、このメインプロセスは行動済みでも行なえ、行なっても行動済みにならない。ただし、このメインプロセスの後あなたはHPを20点失う。このエフェクトは1シナリオ1回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-時の棺', '基本1', 'バロール', '一般', '時の棺', 'トキノヒツキ', '1', 1, 'オートアクション', '—', '自動成功', '単体', '視界', '10', '100%', NULL, '一瞬だけ時間を止め、相手の行動を阻害するエフェクト。たとえ刹那であっても、時間停止という奇跡はあなたに大いなる勝機をもたらす。', '相手が判定を行なう直前に使用する。その判定は失敗となる。「難易度:自動成功」のような、判定を行なわないものには使用できない。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-黒星招来', '基本1', 'バロール', '一般', '黒星招来', 'コクセイショウライ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, 'ブラックホールを形成し、敵の攻撃をすべて飲み込むエフェクト。黒き星の輝きの前に、何人たりともあなたを傷つけることはできない。', 'あなたにHPダメージを適用する直前に使用する。そのHPダメージを0にする。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-黒星の門', '基本1', 'バロール', '一般', '黒星の門', 'コクセイノモン', '3', 3, 'メジャーアクション', 'シンドローム', '—', '—', '—', '2', 'ピュアブリード', NULL, '空間を高重力によって歪ませ、距離をねじ曲げるエフェクト。', 'このエフェクトを組み合わせた場合、「あなたと同じエンゲージにいるキャラクターを対象にできない」と記載されたエフェクトでも、同じエンゲージに対して使用できる。また、このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-アームズリンク', '基本1', 'ブラックドッグ', '一般', 'アームズリンク', 'アームスリンク', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '装備した武器と、自分の神経を直接結線することで、その武器をより効果的に扱うエフェクト。どんな武器でも、あなたにとっては体の一部と同じだ。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-イオノクラフト', '基本1', 'ブラックドッグ', '一般', 'イオノクラフト', 'イオノクラフト', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, 'イオノクラフト効果により、飛行状態で移動するエフェクト。', '飛行状態で戦闘移動を行なう。その際、移動距離を+[LV×2]mする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-雷の加護', '基本1', 'ブラックドッグ', '一般', '雷の加護', 'イカスチノカコ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '時間をかけて体内の電流を増幅するエフェクト。', 'そのメインプロセスの間、あなたが行なうブラックドッグのエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-雷の槍', '基本1', 'ブラックドッグ', '一般', '雷の槍', 'イカスチノヤリ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '2', '—', NULL, '増幅した電流を対象に向かって撃ち出し、攻撃するエフェクト。', '「攻撃力:+[LV×2+4]」の射撃攻撃を行なう。このエフェクトを組み合わせた判定のダイスを−1個する。この攻撃では、あなたと同じエンゲージにいるキャラクターを対象にできない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ウェポンマウント', '基本1', 'ブラックドッグ', '一般', 'ウェポンマウント', 'ウェホンマウント', '3', 3, '常時', '—', '—', '自身', '至近', '—', '—', NULL, '武器を内蔵するエフェクト。', '常備化ポイントが[LV×5]以下の武器をひとつ選択すること。あなたはそのアイテムを入手、常備化できる。選択したアイテムはイニシアチブプロセスで装備、所持の切り替えができる。このエフェクトは侵蝕率によるレベルアップの効果を受けない。このエフェクトを取得した場合、侵蝕率基本値を+2する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-球電の盾', '基本1', 'ブラックドッグ', '一般', '球電の盾', 'キュウテンノタテ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '対象を球電で包み込み、攻撃から守るエフェクト。', 'あなたがガードを行なう際に宣言する。このガードの間、あなたのガード値を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-電磁誘導', '基本1', 'ブラックドッグ', '一般', '電磁誘導', 'テンシユウトウ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '電流の道を形成し、攻撃に正確性を高めるエフェクト。', 'そのメインプロセスの間、あなたが行なうブラックドッグのエフェクトを組み合わせた攻撃に対するリアクションのダイスを−LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-マグネットフォース', '基本1', 'ブラックドッグ', '一般', 'マグネットフォース', 'マクネットフォース', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '磁力の反発などを利用して、対象をかばうエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-雷光撃', '基本1', 'ブラックドッグ', '一般', '雷光撃', 'ライコウケキ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '武器を雷で包み込み、威力を高めるエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-雷神の鎚', '基本1', 'ブラックドッグ', '一般', '雷神の鎚', 'ライシンノツチ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '範囲(選択)', '視界', '3', '—', NULL, '巨大な雷を生みだし、攻撃するエフェクト。', '「攻撃力:+6」の射撃攻撃を行なう。この攻撃では、あなたと同じエンゲージにいるキャラクターを対象にできない。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ライトカスタム', '基本1', 'ブラックドッグ', '一般', 'ライトカスタム', 'ライトカスタム', '1', 1, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'あなたの身体を機械化し、運動能力に特化させたカスタマイズを施すエフェクト。', '【肉体】【感覚】を使用した判定のダイスを+1個する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。このエフェクトを取得した場合、侵蝕率基本値を+2する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-MAXボルテージ', '基本1', 'ブラックドッグ', '一般', 'MAXボルテージ', 'マックスホルテーシ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '4', '80%', NULL, '体内の電流を増幅し、攻撃を強めるエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+10する。ただし、このエフェクトを組み合わせた攻撃の判定のダイスを−1個する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-フルインストール', '基本1', 'ブラックドッグ', '一般', 'フルインストール', 'フルインストール', '3', 3, 'イニシアチブプロセス', '—', '自動成功', '自身', '至近', '5', '100%', NULL, '生体電流の電荷量を上昇させ、神経速度を極限まで速めるエフェクト。', 'そのラウンドの間、あなたが行なうあらゆる判定のダイスを+[LV×3]個する。このエフェクトは1シナリオに1回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-電磁反応装甲', '基本1', 'ブラックドッグ', '一般', '電磁反応装甲', 'テンシハンノウソウコウ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '10', '120%', NULL, '瞬間的に電磁バリアを形成し、攻撃を軽減するエフェクト。', 'HPダメージが適用される直前に使用する。適用される(予定の)HPダメージを−20する。1回のHPダメージに複数回使用した場合、効果は累積する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-雷鳴の申し子', '基本1', 'ブラックドッグ', '一般', '雷鳴の申し子', 'ライメイノモウシコ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '5', 'ピュアブリード', NULL, '肉体の限界を超えて、発電細胞に電撃を生じさせるエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力に+あなたの[最大HP−現在のHP]する。ただし、このエフェクトを使用したメインプロセスの終了時にあなたはHPが0となる。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-赫き剣', '基本1', 'ブラム=ストーカー', '一般', '赫き剣', 'アカキケン', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '血液から白兵戦用の武器を製作するエフェクト。形状はあなたの思いのままである。', '使用時に[LV×2]以下の任意のHPを消費する。そのシーンの間、あなたは以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-赫き弾', '基本1', 'ブラム=ストーカー', '一般', '赫き弾', 'アカキタマ', '5', 5, 'メジャーアクション', '〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '弾丸や矢に自分の血を塗り込めることによって、その武器を強化するエフェクト。', 'このエフェクトを組み合わせた射撃攻撃の攻撃力を+[LV×2]する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを3点失う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-渇きの主', '基本1', 'ブラム=ストーカー', '一般', '渇きの主', 'カワキノアルシ', '5', 5, 'メジャーアクション', '〈白兵〉', '対決', '単体', '至近', '4', '—', NULL, '敵から水分を奪って回復するエフェクト。', 'このエフェクトを組み合わせた白兵攻撃では、対象の装甲値を無視してダメージを算出する。命中した場合、あなたのHPを[LV×4]点回復する。ただし、この攻撃は素手によるものでなければならない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-血族', '基本1', 'ブラム=ストーカー', '一般', '血族', 'ケツソク', '10', 10, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '1', '—', NULL, '血を精密にコントロールし、より高度な力を発揮するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+LVする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-赤河の支配者', '基本1', 'ブラム=ストーカー', '一般', '赤河の支配者', 'セキカノシハイシャ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '血液の流れをコントロールし、血を失わないようにするエフェクト。', 'あなたへのHPダメージが算出された直後に使用する。あなたが受ける(予定の)ダメージを−[1D+LV×2]点する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-鮮血の一撃', '基本1', 'ブラム=ストーカー', '一般', '鮮血の一撃', 'センケツノイチケキ', '5', 5, 'メジャーアクション', '〈白兵〉', '対決', '—', '武器', '2', '—', NULL, '流した血を操って、思わぬところから攻撃を行なうエフェクト。', 'このエフェクトを組み合わせた白兵攻撃のダイスを+[LV+1]個する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを2点失う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-血の宴', '基本1', 'ブラム=ストーカー', '一般', '血の宴', 'チノウタケ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '範囲(選択)', '—', '3', '—', NULL, '血を拡散させて、攻撃範囲を広げるエフェクト。', 'このエフェクトを組み合わせた攻撃の対象を範囲(選択)に変更する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-不死者の恩寵', '基本1', 'ブラム=ストーカー', '一般', '不死者の恩寵', 'フシシャノオンチョウ', '3', 3, 'クリンナッププロセス', '—', '自動成功', '自身', '至近', '5', '—', NULL, '血液中の血小板や白血球を傷口に集中させ、傷を治すエフェクト。', 'あなたのHPを[(LV)D+【肉体】]点回復する。戦闘中ではない場合、このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ブラッドコントロール', '基本1', 'ブラム=ストーカー', '一般', 'ブラッドコントロール', 'フラットコントロール', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '血流をコントロールすることによって、より正確な動きを行なうエフェクト。', 'そのメインプロセス中に行なうブラム=ストーカーのエフェクトの判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-抱擁', '基本1', 'ブラム=ストーカー', '一般', '抱擁', 'ホウヨウ', '1', 1, 'メジャーアクション', '〈RC〉', '自動成功', '単体', '至近', '2', '—', NULL, 'あなたの血液を注入し、死者を復活させるエフェクト。', 'このエフェクトはオーヴァードには効果を適用しない。対象の死亡を回復し、HPを1まで回復する。ただし対象はオーヴァードとして覚醒する可能性がある。詳細はGMが決定すること。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-冥府の棺', '基本1', 'ブラム=ストーカー', '一般', '冥府の棺', 'メイフノヒツキ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, 'わざと心臓を止め、仮死状態になることによって身体機能を再起動させるエフェクト。', '重圧を受けていても使用できる。あなたがバッドステータスを受けた直後に使用する。そのバッドステータスひとつを回復する。ただしバッドステータスの暴走は回復できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ブラッドバーン', '基本1', 'ブラム=ストーカー', '一般', 'ブラッドバーン', 'フラットハーン', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '4', '80%', NULL, '全身の血液をエネルギーに変えて攻撃するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×4]する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを5点失う。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-始祖の血統', '基本1', 'ブラム=ストーカー', '一般', '始祖の血統', 'シソノケットウ', '3', 3, 'メジャーアクション', 'シンドローム', '—', '—', '—', '4', '100%', NULL, 'より大量の血を攻撃に付与するエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+[LV×2]個する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを3点失う。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-不死不滅', '基本1', 'ブラム=ストーカー', '一般', '不死不滅', 'フシフメツ', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, 'あなたが不死であることを表わすエフェクト。', 'あなたが戦闘不能となった時に使用する。あなたは戦闘不能を回復し、HPを[LV×10]まで回復する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-滅びの遺伝子', '基本1', 'ブラム=ストーカー', '一般', '滅びの遺伝子', 'ホロヒノイテンシ', '3', 3, 'オートアクション', '—', '自動成功', '単体', '視界', '6', 'ピュアブリード', NULL, '敵のテロメアを操り瞬時に老化させるエフェクト。', 'あなたが対象から1点でもダメージを受けた時に使用する。対象に[LV×10]点のHPダメージを与える。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-イージスの盾', '基本1', 'キュマイラ', '一般', 'イージスの盾', 'イーシスノタテ', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '相手の攻撃を強引に力で押さえ込むエフェクト。', 'あなたがガードを行なう際に宣言する。このガードの間、あなたのガード値を+(LV)Dする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-完全獣化', '基本1', 'キュマイラ', '一般', '完全獣化', 'カンセンシュウカ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '6', '—', NULL, '肉体を完全な戦闘形態へと変化させるエフェクト。', 'このシーンの間、【肉体】の能力値を使用したあらゆる判定のダイスを+[LV+2]個する。ただし、このエフェクトが持続している間、素手を除くアイテムはすべて装備、使用不可となる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-軍神の守り', '基本1', 'キュマイラ', '一般', '軍神の守り', 'クンシンノマモリ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '研ぎ澄まされた野性の感覚で味方を守護するエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-獣の力', '基本1', 'キュマイラ', '一般', '獣の力', 'ケモノノチカラ', '5', 5, 'メジャーアクション', '〈白兵〉', '対決', '単体', '武器', '2', '—', NULL, '人間には不可能なほどの力で攻撃を行なうエフェクト。', 'このエフェクトを組み合わせた白兵攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-殺意の壁', '基本1', 'キュマイラ', '一般', '殺意の壁', 'サツイノカヘ', '3', 3, 'オートアクション', '—', '自動成功', '単体', '至近', '2', '—', NULL, '相手を威圧する野獣の殺意を発するエフェクト。', 'あなたが白兵攻撃の対象となった時に宣言する。そのメインプロセスの間、対象が行なう白兵攻撃の攻撃力を−[LV×3]する。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ターゲットロック', '基本1', 'キュマイラ', '一般', 'ターゲットロック', 'ターケットロック', '3', 3, 'セットアッププロセス', '—', '自動成功', '単体', '視界', '3', '—', NULL, '獲物に狙いを定めて、攻撃の態勢を整えるエフェクト。', 'そのシーン中、あなたが対象に攻撃を行なった場合、攻撃力を+[LV×3]する。「対象:範囲」のように、対象以外のキャラクターを含めて攻撃する場合、この効果は適用しない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-破壊の爪', '基本1', 'キュマイラ', '一般', '破壊の爪', 'ハカイノツメ', '10', 10, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, 'あなたの腕を鋭い鉤爪へと変化させるエフェクト。', 'そのシーンの間、あなたの素手のデータを以下のように変更する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-鋼の肉体', '基本1', 'キュマイラ', '一般', '鋼の肉体', 'ハカネノニクタイ', '5', 5, 'メジャーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '筋肉の力で傷口を無理やりふさぐエフェクト。', 'HPを[(LV)D+【肉体】]点回復し、現在与えられている暴走以外のバッドステータスすべてを回復する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ハンティングスタイル', '基本1', 'キュマイラ', '一般', 'ハンティングスタイル', 'ハンティンクスタイル', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, '肉食獣のような狩りに適した姿へと変化し、獲物へと接近するエフェクト。', 'あなたは戦闘移動を行なう。この移動では、離脱を行なえる。また、移動中に他のエンゲージに接触しても移動を終える必要はなく、封鎖の影響も受けない。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-魔獣の咆哮', '基本1', 'キュマイラ', '一般', '魔獣の咆哮', 'マシュウノホウコウ', '3', 3, 'オートアクション', '—', '自動成功', '単体', '視界', '4', '—', NULL, '雄叫びによって対象を威圧するエフェクト。', '対象が判定を行なう直前に使用する。その判定のダイスを−LV個する。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-竜鱗', '基本1', 'キュマイラ', '一般', '竜鱗', 'リュウリン', '3', 3, 'リアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '自身の外皮を鱗状に硬質化させ身を守るエフェクト。', 'あなたへの攻撃に対するリアクションとして使用する。その攻撃は命中する代わりに、あなたの装甲値を+[LV×10]してダメージを算出する。この装甲値は他の防具と重複する。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-フルパワーアタック', '基本1', 'キュマイラ', '一般', 'フルパワーアタック', 'フルハワーアタック', '3', 3, 'セットアッププロセス', '—', '自動成功', '自身', '至近', '4', '80%', NULL, '全身の力を集約した構えから一撃で大ダメージを与えるエフェクト。', 'そのラウンド中にあなたが行なう白兵攻撃の攻撃力を+[LV×5]する。ただし、そのラウンドの間、あなたの【行動値】は0となる。このエフェクトによる【行動値】への修正は他のエフェクトに優先する。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-獣の魂', '基本1', 'キュマイラ', '一般', '獣の魂', 'ケモノノタマシイ', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '5', '100%', NULL, 'レネゲイドを活性化して、自身の身体能力を爆発的に上昇させるエフェクト。', 'あなたが【肉体】を使用した技能の判定を行なう直前に使用する。その判定のダイスを+5個する。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-究極獣化', '基本1', 'キュマイラ', '一般', '究極獣化', 'キュウキョクシュウカ', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, '究極といえるほど肉体を強靭なものに変化させるエフェクト。', '《完全獣化》の効果中のみ使用できる。あなたが行なう攻撃のダメージを+(LV)Dし、装甲値を+10する。このエフェクトはシーンが終了するか、《完全獣化》の効果が失われるまで持続する。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-剛身獣化', '基本1', 'キュマイラ', '一般', '剛身獣化', 'コウシンシュウカ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '6', 'ピュアブリード', NULL, '肉体を甲殻などで覆い、プロテクターのようにすることで、攻撃力、防御力を上昇させるエフェクト。', 'このシーンの間、あなたが行なう素手による攻撃の攻撃力を+[LV×2]し、あなたの装甲値を+[LV×3]する。この装甲値は他の防具と重複する。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-うごめく弾丸', '基本1', 'エグザイル', '一般', 'うごめく弾丸', 'ウコメクタンカン', '5', 5, 'メジャーアクション', '〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '弾丸や矢に自分の体組織を同化させ、傷口から相手の邪魔をするエフェクト。', 'このエフェクトを組み合わせた射撃攻撃で、1点でもHPダメージを与えた場合、さらに対象にバッドステータスの重圧を与える。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-オールレンジ', '基本1', 'エグザイル', '一般', 'オールレンジ', 'オールレンシ', '5', 5, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, 'みずからの四肢をより正確に操作するエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-踊る髪', '基本1', 'エグザイル', '一般', '踊る髪', 'オトルカミ', '1', 1, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '髪を飛ばす、動かすなどして、相手を搦め捕るエフェクト。', 'このエフェクトを組み合わせた攻撃で1点でもHPダメージを与えた場合、さらに対象にバッドステータスの硬直を与える。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-壁に耳あり', '基本1', 'エグザイル', '一般', '壁に耳あり', 'カヘニミミアリ', '5', 5, 'メジャーアクション', '〈情報:〉', '—', '—', '—', '2', '—', NULL, '体の一部分を切り離して、独自に情報収集をさせるエフェクト。', 'すべての〈情報:〉に組み合わせることができる。このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-カンビュセスの籤', '基本1', 'エグザイル', '一般', 'カンビュセスの籤', 'カンヒュセスノクシ', '5', 5, 'メジャーアクション', '〈RC〉', '自動成功', '単体', '至近', '2', '—', NULL, 'みずからの体組織を他人に移植して負傷を癒すエフェクト。', 'このエフェクトは自身を対象にできない。対象のHPを[(LV)D+【肉体】]点回復する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-吸収', '基本1', 'エグザイル', '一般', '吸収', 'キュウシュウ', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '対象の一部をあなたの身体の内部に取り込むエフェクト。', 'このエフェクトを組み合わせた攻撃で1点でもHPダメージを与えた場合、そのラウンドの間、対象が行なうあらゆる判定のダイスを−LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-崩れずの群れ', '基本1', 'エグザイル', '一般', '崩れずの群れ', 'クスレスノムレ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '身体の一部分を分裂させて対象をかばうエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-伸縮腕', '基本1', 'エグザイル', '一般', '伸縮腕', 'シンシュクワン', '3', 3, 'メジャーアクション', '〈白兵〉', '対決', '—', '視界', '2', '—', NULL, '手足を伸ばし、対象を攻撃する。', 'このエフェクトを組み合わせた白兵攻撃の射程を視界に変更する。このエフェクトを組み合わせた判定のダイスを−[3−LV]個する(最大0個)。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-骨の銃', '基本1', 'エグザイル', '一般', '骨の銃', 'ホネノシュウ', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '骨を弾丸とする銃を、体内で作り出すエフェクト。', 'そのシーンの間、あなたは以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-骨の剣', '基本1', 'エグザイル', '一般', '骨の剣', 'ホネノツルキ', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '骨の形状を変化させ、白兵用の武器に仕立て上げるエフェクト。', 'そのシーンの間、あなたの素手のデータを以下のように変更する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-歪みの体', '基本1', 'エグザイル', '一般', '歪みの体', 'ユカミノカラタ', '10', 10, 'オートアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, '柔らかい体を生かし、衝撃を逃すエフェクト。', 'あなたがガードを行なう際に宣言する。このガードの間、あなたのガード値を+[LV+3]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-螺旋撃', '基本1', 'エグザイル', '一般', '螺旋撃', 'ラセンケキ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '80%', NULL, '螺旋状に力を蓄え強力な一撃を与えるエフェクト。', 'このメインプロセスの間、あなたが行なうメジャーアクションに対するリアクションは、クリティカル値が+1される。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ジャイアントグロウス', '基本1', 'エグザイル', '一般', 'ジャイアントグロウス', 'シャイアントクロウス', '3', 3, 'メジャーアクション', '〈白兵〉', '対決', '範囲(選択)', '武器', '5', '100%', NULL, '肉体の一部を成長させ、強化するエフェクト。', 'このエフェクトを組み合わせた白兵攻撃の対象を範囲(選択)に変更し、攻撃力を+2Dする。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-透過', '基本1', 'エグザイル', '一般', '透過', 'トウカ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, '肉体を液体と化し攻撃を通過させ、さらにみずからのエネルギーへと変換するエフェクト。', 'あなたへのHPダメージが算出された直後に使用する。あなたに適用される(予定の)HPダメージを0に変更する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-がらんどうの肉体', '基本1', 'エグザイル', '一般', 'がらんどうの肉体', 'カラントウノニクタイ', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '3', 'ピュアブリード', NULL, '体内の重要器官を移動させることでダメージを無効化するエフェクト。', 'あなたへのHPダメージが算出された直後に使用する。あなたが受ける(予定の)ダメージを−(LV)Dする。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-一閃', '基本1', 'ハヌマーン', '一般', '一閃', 'イッセン', '1', 1, 'メジャーアクション', '〈白兵〉', '対決', '—', '武器', '2', '—', NULL, '一瞬にして敵に近づき、攻撃するエフェクト。', 'このエフェクトを組み合わせた場合、全力移動を行なった後に白兵攻撃を行なう。攻撃の成否にかかわらず、移動は行なわれる。ただし、離脱は行なえない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-エンジェルヴォイス', '基本1', 'ハヌマーン', '一般', 'エンジェルヴォイス', 'エンシェルウォイス', '3', 3, 'メジャーアクション', '〈交渉〉', '自動成功', '単体', '視界', '4', '—', NULL, '心地よい高周波を伴った声を出すエフェクト。その出力を調整された高周波は、聞く者の全身を震わせる。', '対象が次に行なうメジャーアクションのクリティカル値を−1(下限値6)し、判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-風の渡し手', '基本1', 'ハヌマーン', '一般', '風の渡し手', 'カセノワタシテ', '5', 5, 'メジャーアクション', '〈交渉〉', '—', '[LV+1]体', '—', '3', '—', NULL, '音自体を操って、対象のみに声を届かせるエフェクト。あなたは空間に発生する波そのものを操って特定領域にのみ音を響かせることができる。', 'このエフェクトを組み合わせた行動の対象を[LV+1]体に変更する。このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-サイレンの魔女', '基本1', 'ハヌマーン', '一般', 'サイレンの魔女', 'サイレンノマショ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', 'シーン(選択)', '視界', '5', '—', NULL, '周囲全域に振動波の攻撃を加えるエフェクト。', '「攻撃力:+[LV×3]」の射撃攻撃を行なう。このエフェクトを組み合わせた攻撃では、対象の装甲値を無視してダメージを算出する。このエフェクトを組み合わせた判定には、《コンセントレイト》を組み合わせることはできない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-さらなる波', '基本1', 'ハヌマーン', '一般', 'さらなる波', 'サラナルナミ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '波のエネルギーを増幅し、攻撃力を増強するエフェクト。あなたの力と共鳴し、振動のエネルギーは無限に高まっていく。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-獅子奮迅', '基本1', 'ハヌマーン', '一般', '獅子奮迅', 'シシフンシン', '3', 3, 'メジャーアクション', '〈白兵〉', '対決', '範囲(選択)', '武器', '4', '—', NULL, '同時に何撃もの攻撃をくり出し、あなたの周囲のすべての敵をなぎ倒すエフェクト。何百人の敵であろうと、あなたにかかれば一瞬である。', 'このエフェクトを組み合わせた白兵攻撃の対象を範囲(選択)に変更する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-振動球', '基本1', 'ハヌマーン', '一般', '振動球', 'シントウキュウ', '10', 10, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '3', '—', NULL, '振動波を打ち出して敵を攻撃するエフェクト。どんなに分厚い装甲も、振動波の前には無力だ。', '「攻撃力:+LV」の射撃攻撃を行なう。この攻撃では、対象の装甲値を無視してダメージを算出する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-声援', '基本1', 'ハヌマーン', '一般', '声援', 'セイエン', '3', 3, 'メジャーアクション', '〈交渉〉', '自動成功', '—', '視界', '2', '—', NULL, '声を媒介に、対象の運動能力、思考能力を増幅する“波”を送り込むエフェクト。', '対象が次に行なうメジャーアクションのダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-電光石火', '基本1', 'ハヌマーン', '一般', '電光石火', 'テンコウセッカ', '3', 3, 'メジャーアクション/リアクション', '【肉体】【感覚】', '—', '—', '—', '3', '—', NULL, '肉体の限界を遙かに超えたスピードと反射神経を発揮するエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを1D失う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-バトルビート', '基本1', 'ハヌマーン', '一般', 'バトルビート', 'ハトルヒート', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '独特のリズムで戦うことで、戦闘を有利に進めるエフェクト。内なる鼓動を把握し、そのビートに乗って戦うことで、あなたは敵を翻弄する。', 'そのメインプロセスの間、あなたが行なうハヌマーンのエフェクトを使用した判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-吠え猛る爪', '基本1', 'ハヌマーン', '一般', '吠え猛る爪', 'ホエタケルツメ', '5', 5, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '振動波を伝達させ、あらゆる装甲を無視してダメージを与えるエフェクト。あなたの振動波は、物質分子結合を分解する効果を持つ。', 'このエフェクトを組み合わせた攻撃では、対象の装甲値を無視してダメージを算出し、攻撃力を−[5−LV](最大0)する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-マシラのごとく', '基本1', 'ハヌマーン', '一般', 'マシラのごとく', 'マシラノコトク', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '単体', '—', '5', '80%', NULL, '高速化された神経伝達による超高速の連撃を行なうエフェクト。ただし、そのあまりの速さに制御は難しい。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×10]する。ただし、このエフェクトを組み合わせた判定のダイスは−5個される。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-リミットリリース', '基本1', 'ハヌマーン', '一般', 'リミットリリース', 'リミットリリース', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '6', '100%', NULL, '神経加速により限界を突破するエフェクト。', 'あなたが判定を行なう直前に使用する。その判定のクリティカル値を−1(下限値5)する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-空蝉', '基本1', 'ハヌマーン', '一般', '空蝉', 'ウツセミ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, '残像を残して高速移動し、敵の攻撃を自身からそらすエフェクト。', 'あなたへのHPダメージが算出された直後に使用する。そのHPダメージを0にする。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-スピードフォース', '基本1', 'ハヌマーン', '一般', 'スピードフォース', 'スヒートフォース', '3', 3, 'イニシアチブプロセス', '—', '自動成功', '自身', '至近', '4', 'ピュアブリード', NULL, 'とてつもない速度によって、敵に反応させず行動するエフェクト。', 'あなたはイニシアチブプロセスにメインプロセスを行なえる。このエフェクトはあなたが未行動でないと使用できない。このエフェクトは1シナリオにLV回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-インフィニティウェポン', '基本1', 'モルフェウス', '一般', 'インフィニティウェポン', 'インフィニティウェホン', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '物質を変換して、白兵戦用の武器を製作するエフェクト。その形状はあなたの思いのままである。', 'そのシーンの間、以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-黄金錬成', '基本1', 'モルフェウス', '一般', '黄金錬成', 'オウコンレンセイ', '3', 3, '常時', '—', '自動成功', '自身', '至近', '—', '—', NULL, '物質を黄金に変換するエフェクト。', 'キャラクター作成時、およびアフタープレイにおける常備化ポイントの計算の際、常備化ポイントを+[LV×3]する。このエフェクトは侵蝕率でレベルアップしない。このエフェクトを取得した場合、侵蝕率基本値が+3される。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-カスタマイズ', '基本1', 'モルフェウス', '一般', 'カスタマイズ', 'カスタマイス', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, '武器を最も攻撃に適した形に変えるエフェクト。あなたの手に馴染むように武器が変形する。それは世界でひとつだけのあなた専用の武器だ。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ギガンティックモード', '基本1', 'モルフェウス', '一般', 'ギガンティックモード', 'キカンティックモート', '1', 1, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '範囲(選択)', '武器', '3', '—', NULL, '武器を分裂させるエフェクト。無数に分かたれた武器の切っ先は、並みいる敵を的確に倒していく。', 'このエフェクトを組み合わせた攻撃の対象を範囲(選択)に変更する。ただし、判定後にあなたの装備している武器はすべて破壊される。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-サイコメトリー', '基本1', 'モルフェウス', '一般', 'サイコメトリー', 'サイコメトリー', '3', 3, 'メジャーアクション', '〈情報:〉', '—', '—', '—', '2', '—', NULL, '物品から情報を引き出すエフェクト。壁や床などから、その場所で起きたことも引き出すことができる。', 'すべての〈情報:〉判定と組み合わせることができる。このエフェクトを組み合わせた判定のダイスを+[LV+2]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-シールドクリエイト', '基本1', 'モルフェウス', '一般', 'シールドクリエイト', 'シールトクリエイト', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '物質を変換して、盾を製作するエフェクト。その形状はあなたの思いのままである。', 'そのシーンの間、以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-水晶の剣', '基本1', 'モルフェウス', '一般', '水晶の剣', 'スイショウノケン', '3', 3, 'メジャーアクション', '—', '自動成功', '効果参照', '至近', '4', '—', NULL, '触れた物質を結晶化させて粉砕する水晶を武器にまとわせるエフェクト。', '武器をひとつ選択すること。そのシナリオの間、選択した武器の攻撃力を+[LV×2]する。このエフェクトは1シナリオに3回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-砂の結界', '基本1', 'モルフェウス', '一般', '砂の結界', 'スナノケッカイ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '砂で相手をかばうエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ハンドレッドガンズ', '基本1', 'モルフェウス', '一般', 'ハンドレッドガンズ', 'ハントレットカンス', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '物質を変換して、射撃戦用の武器を製作するエフェクト。その形状はあなたの思いのままである。', 'そのシーンの間、以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ペネトレイト', '基本1', 'モルフェウス', '一般', 'ペネトレイト', 'ヘネトレイト', '1', 1, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '3', '—', NULL, '武器の形状を変化させ、貫通力を高めるエフェクト。', 'このエフェクトを組み合わせた攻撃では、対象の装甲値を無視してダメージを算出する。ただし、このエフェクトを組み合わせた判定のダイスを−1個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-レインフォース', '基本1', 'モルフェウス', '一般', 'レインフォース', 'レインフォース', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '物質の強度を上げることによって、その攻撃力を向上するエフェクト。あなたの操る武器は、ダイヤモンド以上の硬度を誇る。その手にかかれば、たとえ古びた剣でも名剣と変わるだろう。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-パーフェクトコントロール', '基本1', 'モルフェウス', '一般', 'パーフェクトコントロール', 'ハーフェクトコントロール', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '4', '80%', NULL, '物体の構造を完全に理解し、一瞬だけ完全無欠にそれを使いこなすエフェクト。', 'あなたが判定を行なう直前に使用する。その判定の達成値を+10する。ただしあなたは、その判定後にHPを5点失う。このエフェクトは1シーンに1回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-クリスタライズ', '基本1', 'モルフェウス', '一般', 'クリスタライズ', 'クリスタライス', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '4', '100%', NULL, '攻撃を当てた場所から、対象を結晶化させて砕くエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×3]し、対象の装甲値を無視してダメージを算出する。このエフェクトは1シナリオに3回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-魂の錬成', '基本1', 'モルフェウス', '一般', '魂の錬成', 'タマシイノレンセイ', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '4D10', '120%', NULL, 'みずからの魂を物質変換で作り上げるエフェクト。', 'あなたが戦闘不能となった時に使用する。あなたは戦闘不能を回復し、HPを[LV×10]まで回復する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-錬成の掟', '基本1', 'モルフェウス', '一般', '錬成の掟', 'レンセイノオキテ', '3', 3, 'オートアクション', '—', '自動成功', '効果参照', '—', '4', 'ピュアブリード', NULL, 'より機能の高い物を作り出すエフェクト。', 'いつでも宣言できる。あなたがエフェクトによって作成した武器、防具、ヴィークルひとつを対象とする。そのシーンの間、そのアイテムの攻撃力、ガード値、装甲値のいずれかひとつに+5する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-アドヴァイス', '基本1', 'ノイマン', '一般', 'アドヴァイス', 'アトウァイス', '5', 5, 'メジャーアクション', '〈交渉〉', '自動成功', '単体', '視界', '4', '—', NULL, '的確な助言を行なうことで対象の行動をより正確なものにするエフェクト。', '次に対象が行なうメジャーアクションのクリティカル値を−1(下限値6)し、その判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-インスピレーション', '基本1', 'ノイマン', '一般', 'インスピレーション', 'インスヒレーション', '3', 3, 'メジャーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '直感によって、過程を飛ばして正解にたどり着くエフェクト。それは天才だけがもつひらめきの力だ。', 'GMに対して疑問点を直接質問できる。GMは回答を拒否できるが、その場合は使用回数に数えない。このエフェクトは1シナリオにLV回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-オウガバトル', '基本1', 'ノイマン', '一般', 'オウガバトル', 'オウカハトル', '10', 10, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '戦闘の天才性を表わすエフェクト。あなたの戦いはまるで鬼神のようで、絶大な破壊と混乱とをもたらす。', 'そのメインプロセスの間、あなたが行なう攻撃のダメージを+[LV+2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-コントロールソート', '基本1', 'ノイマン', '一般', 'コントロールソート', 'コントロールソート', '1', 1, 'メジャーアクション', '効果参照', '対決', '—', '武器', '2', '—', NULL, '思考で身体を制御するエフェクト。', 'このエフェクトの取得時に、〈白兵〉か〈射撃〉のどちらかを選択すること。選択した方を、このエフェクトの使用技能とする。このエフェクトを組み合わせた判定は【精神】で判定を行なえる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-コンバットシステム', '基本1', 'ノイマン', '一般', 'コンバットシステム', 'コンハットシステム', '3', 3, 'メジャーアクション/リアクション', '効果参照', '対決', '—', '—', '3', '—', NULL, 'あなたがまるで組み上げられたプログラムのように、正確に戦闘を行なえることを表わすエフェクト。', 'このエフェクトの取得時に、〈白兵〉か〈射撃〉のどちらかを選択すること。選択した方を、このエフェクトの使用技能とする。このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-戦術', '基本1', 'ノイマン', '一般', '戦術', 'センシュツ', '5', 5, 'セットアッププロセス', '—', '自動成功', 'シーン(選択)', '視界', '6', '—', NULL, '仲間に的確な戦術的指示を飛ばすことによって、その行動をフォローするエフェクト。', '対象がこのラウンド中に行なうメジャアクションのダイスを+LV個する。このエフェクトはあなたを対象に選択できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-デュアルデュエル', '基本1', 'ノイマン', '一般', 'デュアルデュエル', 'テュアルテュエル', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '2', '—', NULL, 'フェイントを織り交ぜた連続攻撃を行なうことによって、必殺の一撃への布石を作るエフェクト。', 'このエフェクトを組み合わせた攻撃に対するドッジのクリティカル値を+1する。ただし、この効果でクリティカル値が10を上回ることはない。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-天性のひらめき', '基本1', 'ノイマン', '一般', '天性のひらめき', 'テンセイノヒラメキ', '3', 3, 'メジャーアクション', '効果参照', '—', '—', '—', '4', '—', NULL, '瞬間のひらめきによって、あらゆる行動の正解を見切るエフェクト。', 'あらゆる能力値判定および技能判定に組み合わせることができる。その判定のクリティカル値を−[LV(下限値7)]する。ただし、このエフェクトは戦闘中は使用できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-抜き打ち', '基本1', 'ノイマン', '一般', '抜き打ち', 'ヌキウチ', '5', 5, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '3', '—', NULL, '構えると同時に攻撃を繰り出すことで、相手の意表を突くエフェクト。', 'このエフェクトに対するドッジの判定のダイスを−[LV×2]個する。また、この時、装備する武器を変更してもよい。このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ブラックマーケット', '基本1', 'ノイマン', '一般', 'ブラックマーケット', 'フラックマーケット', '5', 5, '常時', '—', '—', '自身', '至近', '—', '—', NULL, '市場の動きや情報などに手を回して、うまく望みのアイテムを手に入れるエフェクト。', 'あなたの常備化ポイントを+[LV×3]する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。このエフェクトを取得した場合、侵蝕率基本値を+2する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-マルチウェポン', '基本1', 'ノイマン', '一般', 'マルチウェポン', 'マルチウェホン', '1', 1, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '3', '—', NULL, '複数の武器を使用するエフェクト。', '同じ技能で扱う武器の攻撃力をふたつ合計して使用できる。ふたつの武器は両方とも装備していなければならない。射程はもっとも短いもの、攻撃範囲はもっとも狭いものを適用する。同一エンゲージに攻撃できない武器と攻撃できる武器を組み合わせた場合は、前者に従う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-カウンター', '基本1', 'ノイマン', '一般', 'カウンター', 'カウンター', '3', 3, 'リアクション', '〈白兵〉〈射撃〉', '対決', '単体', '武器', '4', '80%', NULL, NULL, '対象があなたに対し、「対象:単体」の攻撃を行ってきた際に、リアクションとして使用できる。対象に攻撃を行なう。この際、「タイミング:メジャーアクション」のエフェクトと組み合わせてよい。対決に勝利した側の攻撃が命中する。未行動でのみ使用でき、使用後に行動済みになる。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ラストアクション', '基本1', 'ノイマン', '一般', 'ラストアクション', 'ラストアクション', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '5', '100%', NULL, '倒れる間際に、強引に行動を取るエフェクト。', 'あなたが戦闘不能になった瞬間に使用できる。あなたは即座にメインプロセスを行なうことができる。このメインプロセスは行動済みでも行なえ、行なっても行動済みにならない。また、このメインプロセスが終了するまで、戦闘不能の効果は適用されない。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-戦神の祝福', '基本1', 'ノイマン', '一般', '戦神の祝福', 'センシンノシュクフク', '3', 3, 'メジャーアクション', '〈白兵〉〈射撃〉', '対決', '—', '武器', '20', '120%', NULL, 'ノイマンネットワークをフルに活用し、最適のタイミング、最大の火力で攻撃するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV+4]Dする。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-インタラプト', '基本1', 'ノイマン', '一般', 'インタラプト', 'インタラフト', '3', 3, 'オートアクション', '—', '自動成功', '単体', '視界', '8', 'ピュアブリード', NULL, '仲間とタイミングを合わせて妨害を仕掛けることで、相手の集中力を削ぐエフェクト。', '対象が判定を行なう直前に使用する。その判定のクリティカル値を+1する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-アニマルテイマー', '基本1', 'オルクス', '一般', 'アニマルテイマー', 'アニマルテイマー', '3', 3, 'メジャーアクション/リアクション', 'シンドローム', '—', '—', '—', '3', '—', NULL, 'あなたの“因子”を動物に埋め込み、その動物を意のままに操ることによって、行動の補助をさせるエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-要の陣形', '基本1', 'オルクス', '一般', '要の陣形', 'カナメノシンケイ', '3', 3, 'メジャーアクション', 'シンドローム', '—', '3体', '—', '3', '—', NULL, '領域を利用して、複数を対象にするエフェクト。', 'このエフェクトを組み合わせた行動の対象を3体に変更する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ジャミング', '基本1', 'オルクス', '一般', 'ジャミング', 'シャミンク', '3', 3, 'オートアクション', '—', '自動成功', '単体', '視界', '4', '—', NULL, '領域内の物体を操り、対象の攻撃を阻害するエフェクト。', '対象が判定を行なう直前に使用する。その判定のダイスを−LV個する。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-縮地', '基本1', 'オルクス', '一般', '縮地', 'シュクチ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '領域内の距離を操作し、自由に移動するエフェクト。', 'あなたが戦闘移動、または全力移動を行なう直前に使用する。その移動では、シーンの任意の場所に移動できる。この移動では離脱を行なえる。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-絶対の空間', '基本1', 'オルクス', '一般', '絶対の空間', 'セッタイノクウカン', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '領域をコントロールし、自分の能力を最大限に発揮するエフェクト。', 'そのメインプロセスの間、あなたが行なうオルクスのエフェクトを使用した判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-大地の加護', '基本1', 'オルクス', '一般', '大地の加護', 'タイチノカコ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '領域を操って、“地の利”を発生させつつ攻撃するエフェクト。', 'このエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-大地の牙', '基本1', 'オルクス', '一般', '大地の牙', 'タイチノキハ', '10', 10, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '1', '—', NULL, '大地を槍のように隆起、射出するエフェクト。', '「攻撃力:+[LV+2]」の射撃攻撃を行なう。この攻撃に対するドッジの判定のダイスを−1個する。あなたと同じエンゲージにいるキャラクターを対象にできない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-破砕の顎', '基本1', 'オルクス', '一般', '破砕の顎', 'ハサイノアコ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '範囲(選択)', '視界', '3', '—', NULL, '連続して、地面から岩石を槍のように隆起させるエフェクト。', '「攻撃力:+[LV×2+2]」の射撃攻撃を行なう。この攻撃に対するドッジの判定のダイスを−2個する。あなたと同じエンゲージにいるキャラクターを対象にできない。このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ハンドリング', '基本1', 'オルクス', '一般', 'ハンドリング', 'ハントリンク', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '1', '—', NULL, '支配した動物をみずからの代わりにシーンに登場させるエフェクト。', 'シーン登場時に使用する。あなたの代わりに、支配下の動物を登場させる。そのシーンの間、あなたは〈知覚〉判定の達成値を+[LV×3]し、あなたは《アニマルテイマー》を使用できない。あなたと動物は、能力値、ダメージなどを共有して扱うこと。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-導きの華', '基本1', 'オルクス', '一般', '導きの華', 'ミチヒキノハナ', '5', 5, 'メジャーアクション', '〈RC〉〈交渉〉', '自動成功', '単体', '視界', '2', '—', NULL, '領域を調整することで、他者の行動を阻害しそうな要素を省くエフェクト。', '対象が次に行なうメジャーアクションの判定の達成値を+[LV×2]する。ただし、購入判定には効果を発揮しない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-力場の形成', '基本1', 'オルクス', '一般', '力場の形成', 'リキハノケイセイ', '5', 5, 'セットアッププロセス', '—', '自動成功', '単体', '視界', '3', '—', NULL, '攻撃能力を増加させる領域を形成するエフェクト。', 'そのラウンドの間、対象の行なうあらゆる攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-束縛の領域', '基本1', 'オルクス', '一般', '束縛の領域', 'ソクハクノリョウイキ', '1', 1, 'オートアクション', '〈交渉〉', '効果参照', '自身', '至近', '5', '80%', NULL, NULL, 'あなたから「射程:視界」に存在するキャラクターが攻撃の判定を行なった直後に使用する。その攻撃の判定の達成値を難易度として、このエフェクトを組み合わせた判定を行なう。成功した場合、その攻撃は失敗となる。このエフェクトは「タイミング:リアクション」のエフェクトと組み合わせることができ、1シーンに1回まで使用できる。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-完全なる世界', '基本1', 'オルクス', '一般', '完全なる世界', 'カンセンナルセカイ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '6', '100%', NULL, '領域内の全方位から同時に攻撃を仕掛けるエフェクト。', 'このエフェクトを組み合わせた攻撃の判定のダイスを+[LV+1]個する。また、この攻撃に対するリアクションのクリティカル値を+1する。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ナーブジャック', '基本1', 'オルクス', '一般', 'ナーブジャック', 'ナーフシャック', '1', 1, 'メジャーアクション', '〈RC〉', '対決', '単体', '視界', '4D10', '120%', NULL, '対象を支配するエフェクト。', '対象の〈意志〉と対決を行なう。あなたが勝利した場合、即座に対象にメジャーアクションを1回行なわせる。行動の内容はあなたが決定すること。ただし、回数制限のあるエフェクトは使用させられないものとする。このエフェクトは他のいかなるエフェクトの効果によっても対象を変更できない。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-支配の因子', '基本1', 'オルクス', '一般', '支配の因子', 'シハイノインシ', '3', 3, 'セットアッププロセス', '—', '自動成功', 'シーン(選択)', '視界', '6', 'ピュアブリード', NULL, '領域内のすべてを支配、掌握し、敵の動きを阻害するエフェクト。', 'そのラウンドの間、対象が行なう攻撃の攻撃力を−[LV×5](最低0)する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-蒼き悪魔', '基本1', 'サラマンダー', '一般', '蒼き悪魔', 'アオキアクマ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '攻撃の軌跡をさかのぼって、相手を凍らせるエフェクト。', 'あなたがガードを行なう際に宣言する。あなたを攻撃してきたキャラクターに[LV×3]点のHPダメージを与える。このエフェクトは1ラウンドに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-炎陣', '基本1', 'サラマンダー', '一般', '炎陣', 'エンシン', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '炎を自分の周囲に発生させ、それによって味方をかばうエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-炎神の怒り', '基本1', 'サラマンダー', '一般', '炎神の怒り', 'エンシンノイカリ', '3', 3, 'メジャーアクション/リアクション', '【肉体】', '—', '—', '—', '3', '—', NULL, 'みずからを焼くほどの炎を全身にまとい、戦闘力を上昇させるエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを3点失う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-陽炎', '基本1', 'サラマンダー', '一般', '陽炎', 'カケロウ', '3', 3, 'リアクション', '〈回避〉', '対決', '自身', '至近', '3', '—', NULL, '熱による陽炎を作り出して敵の目をあざむき、その隙に攻撃を回避するエフェクト。手を伸ばせばそこにあるように感じられるのに、触れることが決してできないもの。それが陽炎だ。', 'あなたはドッジを行なう。その判定のダイスを+[LV+1]個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-禁息', '基本1', 'サラマンダー', '一般', '禁息', 'キンソク', '1', 1, 'メジャーアクション', '〈RC〉', '対決', '範囲(選択)', '至近', '2', '—', NULL, '対象の周囲の酸素を急激に燃焼させることで酸欠を起こさせるエフェクト。', '射撃攻撃を行なう。命中した場合、対象にバッドステータスの放心を与える。このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-氷の加護', '基本1', 'サラマンダー', '一般', '氷の加護', 'コオリノカコ', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '空気中の水分を結晶化させて氷の刃と変え、次にあなたが行なう攻撃とともにたたき込むエフェクト。', 'そのメインプロセスの間、あなたが行なうサラマンダーのエフェクトを組み合わせた攻撃の攻撃力を+[LV×2]する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-氷の塔', '基本1', 'サラマンダー', '一般', '氷の塔', 'コオリノトウ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '範囲(選択)', '視界', '3', '—', NULL, '空気中の水分を急速に冷凍し、氷の弾丸に変えてたたきつけるエフェクト。その有様は、突如として氷の柱が現出したようにも見える。', '「攻撃力:+[LV×3]」の射撃攻撃を行なう。あなたと同じエンゲージにいるキャラクターを対象にできない。このエフェクトは1シーンに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-焦熱の弾丸', '基本1', 'サラマンダー', '一般', '焦熱の弾丸', 'ショウネツノタンカン', '10', 10, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '1', '—', NULL, '炎の弾丸を投射して相手を焼き尽くすエフェクト。それはサラマンダーにとってもっとも初歩的な、そして根源的な攻撃手段である。', '「攻撃力:+[LV+2]」の射撃攻撃を行なう。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-氷炎の剣', '基本1', 'サラマンダー', '一般', '氷炎の剣', 'ヒョウエンノケン', '5', 5, 'マイナーアクション', '—', '自動成功', '自身', '至近', '3', '—', NULL, '大気中の水分を凍らせてあるいは炎をプラズマ誘導して形にして、武器を手の中に生み出すエフェクト。形状はあなたが自在に決めてよい。', 'そのシーンの間、以下のデータの武器を作成し、装備する。', 'EA IA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-氷壁', '基本1', 'サラマンダー', '一般', '氷壁', 'ヒョウヘキ', '1', 1, 'オートアクション', '〈RC〉', '効果参照', '自身', '至近', '5', '—', NULL, NULL, 'あなたから「射程:視界」に存在するキャラクターが攻撃の判定を行なった直後に使用する。その判定の達成値を目標値とし、このエフェクトを組み合わせた判定を行なう。成功した場合、その攻撃は失敗となる。このエフェクトは「タイミング:リアクション」のエフェクトと組み合わせることができる。未行動でのみ使用でき、使用後に行動済みとなる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-冷気の鎌', '基本1', 'サラマンダー', '一般', '冷気の鎌', 'レイキノカマ', '5', 5, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '2', '—', NULL, '武器に触れるだけで対象を凍結させるほどの超低温の冷気をまとわせ攻撃するエフェクト。その冷気は相手の動きを鈍らせ、回避を極度に困難にする。', 'このエフェクトを組み合わせた攻撃に対するドッジは、判定のダイスが−LV個される。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-終末の炎', '基本1', 'サラマンダー', '一般', '終末の炎', 'シュウマツノホノオ', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '80%', NULL, '自分自身を燃焼させることによりダメージを上昇させるエフェクト。', '[LV×5]点までの任意のHPを消費する。そのメインプロセスの間、あなたが行なう攻撃の攻撃力を+[消費したHP]する。HPが0になるようにHPを消費することはできない。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-プラズマカノン', '基本1', 'サラマンダー', '一般', 'プラズマカノン', 'フラスマカノン', '3', 3, 'メジャーアクション', '〈RC〉', '対決', '単体', '視界', '4', '100%', NULL, 'あらゆる物体を蒸発させるだけの熱量をもったプラズマ球を作り出し、相手めがけて発射するエフェクト。その威力は圧倒的だ。', '「攻撃力:+[LV×5]」の射撃攻撃を行なう。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-極大消滅波', '基本1', 'サラマンダー', '一般', '極大消滅波', 'キョクタイショウメツハ', '5', 5, 'イニシアチブプロセス', '—', '自動成功', '範囲(選択)', '視界', '4D10', '120%', NULL, '純粋なエネルギー力場を現出させ、対象を消滅させるエフェクト。', '対象に[LV+2]D点のHPダメージを与える。このエフェクトに命中判定はなく、対象はリアクションを行なうこともできない。このエフェクトは1シナリオに1回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-結合粉砕', '基本1', 'サラマンダー', '一般', '結合粉砕', 'ケツコウフンサイ', '3', 3, 'メジャーアクション', 'シンドローム', '対決', '—', '—', '4', 'ピュアブリード', NULL, '超高温と超低温とを交互にたたきつけ、分子間に働く結合力を破壊するエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。さらに、対象の装甲値を無視してダメージを算出する。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-抗いがたき言葉', '基本1', 'ソラリス', '一般', '抗いがたき言葉', 'アラカイカタキコトハ', '5', 5, 'メジャーアクション', '〈交渉〉', '対決', '単体', '視界', '2', '—', NULL, '幻覚物質を生成し、体表から発散することで、逆らえない命令を下すエフェクト。', '射撃攻撃を行なう。命中した場合、そのシーンの間、対象が行なうあらゆる判定のダイスを−LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-癒しの水', '基本1', 'ソラリス', '一般', '癒しの水', 'イヤシノミス', '5', 5, 'メジャーアクション', '〈RC〉', '自動成功', '—', '視界', '2', '—', NULL, '細胞の再生能力を増大させる物質を生成し、対象の負傷を治癒するエフェクト。', '対象のHPを[(LV)D+【精神】]点回復する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-錯覚の香り', '基本1', 'ソラリス', '一般', '錯覚の香り', 'サッカクノカオリ', '3', 3, 'メジャーアクション/リアクション', '〈交渉〉', '—', '—', '—', '2', '—', NULL, '安心感を与える香りを生成および発散して、対象の警戒心をとくエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+LV個する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-絶対の恐怖', '基本1', 'ソラリス', '一般', '絶対の恐怖', 'セッタイノキョウフ', '5', 5, 'メジャーアクション', '〈交渉〉', '対決', '—', '視界', '3', '—', NULL, '対象を恐怖させる幻覚を見せるエフェクト。', '「攻撃力:+LV」の射撃攻撃を行なう。この攻撃では、対象の装甲値を無視してダメージを算出する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-中和剤', '基本1', 'ソラリス', '一般', '中和剤', 'チュウワサイ', '1', 1, 'メジャーアクション', '〈RC〉', '自動成功', '—', '視界', '2', '—', NULL, '中和剤を生成し、投与することで、対象の不調や毒などを回復させるエフェクト。', '対象の受けている暴走以外のバッドステータスをすべて回復する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-毒の刃', '基本1', 'ソラリス', '一般', '毒の刃', 'トクノヤイハ', '10', 10, 'マイナーアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '体内で生成した神経毒を、てのひらや粘膜などから分泌し、攻撃の威力を増大させるエフェクト。', 'そのメインプロセスの間、あなたの行なう攻撃の攻撃力を+LVする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-トランキリティ', '基本1', 'ソラリス', '一般', 'トランキリティ', 'トランキリティ', '5', 5, 'メジャーアクション/リアクション', '【精神】', '—', '—', '—', '3', '—', NULL, '精神を安定させる物質を生成し、心の平静を保つことで、思考を鋭くするエフェクト。', 'このエフェクトを組み合わせた判定のダイスを+[LV+1]個する。ただし、このエフェクトを使用したメインプロセス終了時に、あなたはHPを3点失う。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-熱狂', '基本1', 'ソラリス', '一般', '熱狂', 'ネッキョウ', '5', 5, 'メジャーアクション', '〈RC〉', '自動成功', '単体', '視界', '2', '—', NULL, '特殊な物質で理性を失わせ、代わりに怪力を与えるエフェクト。', 'そのシーンの間、対象の行なう白兵攻撃の攻撃力を+[LV×3]する。ただし、対象はバッドステータスの暴走を受ける。効果に同意する対象にしか使用できない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ポイズンフォッグ', '基本1', 'ソラリス', '一般', 'ポイズンフォッグ', 'ホイスンフォック', '3', 3, 'メジャーアクション', 'シンドローム', '—', '範囲(選択)', '至近', '2', '—', NULL, '体内で生成した物質を気体として全身から発散、周囲に散布するエフェクト。', 'このエフェクトを組み合わせたエフェクトの射程を至近、対象を範囲(選択)に変更する。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ヨモツへグリ', '基本1', 'ソラリス', '一般', 'ヨモツへグリ', 'ヨモツヘクリ', '3', 3, 'メジャーアクション', '〈RC〉', '自動成功', '単体', '至近', '6', '—', NULL, '強力な蘇生薬を生成し、投与するエフェクト。', '対象の戦闘不能を回復し、HPを1まで回復する。ただし、対象の侵蝕率は2D点上昇する。この時、非オーヴァードはオーヴァードとして覚醒する可能性がある。詳しくはGMが決めること。このエフェクトは1シナリオにLV回使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-流血の胞子', '基本1', 'ソラリス', '一般', '流血の胞子', 'リュウケツノホウシ', '5', 5, 'メジャーアクション', '〈RC〉', '対決', '—', '視界', '2', '—', NULL, '細胞を崩壊させる胞子を生成し、散布するエフェクト。', '射撃攻撃を行なう。命中した場合、対象にバッドステータスの邪毒を与える。邪毒のランクはLVとする。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-狂戰士', '基本1', 'ソラリス', '一般', '狂戰士', 'キョウセンシ', '3', 3, 'メジャーアクション', '〈RC〉', '自動成功', '単体', '視界', '5', '80%', NULL, '強力な覚醒物質を生成し、投与するエフェクト。', '対象が次に行なうメジャーアクションの判定のクリティカル値を−1(下限値6)し、さらに、その判定のダイスを+[LV×2]個する。', 'EA', 1);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-オーバードーズ', '基本1', 'ソラリス', '一般', 'オーバードーズ', 'オーハートース', '1', 1, 'メジャーアクション/リアクション', 'シンドローム', '—', '—', '—', '4', '100%', NULL, '生成する物質の量を増加させ、過剰に投与するエフェクト。', 'このエフェクトと組み合わせた判定では、組み合わせたエフェクトすべてのLVを+2する。この時、LVの上限を超えてもよい。ただし、この効果でエフェクトの使用回数は増加しない。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 2);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-アクアウィターエ', '基本1', 'ソラリス', '一般', 'アクアウィターエ', 'アクアウィターエ', '3', 3, 'オートアクション', '—', '自動成功', '単体', '視界', '10', '120%', NULL, '生命の精髄ともいえる物質を用いて命を呼び戻すエフェクト。', '対象が戦闘不能となった時に使用する。対象の戦闘不能を回復し、HPを[LV×10]まで回復する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 3);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-女王の降臨', '基本1', 'ソラリス', '一般', '女王の降臨', 'ショオウノコウリン', '3', 3, 'セットアッププロセス', '—', '自動成功', '自身', '至近', '5', 'ピュアブリード', NULL, '複数の体内工場を起動し、超速で薬品生成を行なう。', 'あなたが取得している「タイミング:メジャーアクション」かつ「難易度:自動成功」のソラリスのエフェクトひとつを使用する。侵蝕値は合計し、上昇させること。1シナリオにLV回まで使用できる。', 'EA', 5);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-リザレクト', '基本1', '一般', '一般', 'リザレクト', 'リサレクト', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '効果参照', '—', NULL, 'みずからの受けた損傷を回復するエフェクト。', 'あなたが戦闘不能になった時か、シーンの終了時に使用する。あなたは戦闘不能を回復し、HPを(LV)D点回復する。回復したHPと同じだけ、あなたの侵蝕率が上昇する。このエフェクトは侵蝕率100%以上では使用できない。このエフェクトは重圧を受けていても使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ワーディング', '基本1', '一般', '一般', 'ワーディング', 'ワーティンク', '1', 1, 'オートアクション', '—', '自動成功', 'シーン', '視界', '0', '—', NULL, '特殊な物質を空中に散布し、一般人を無力化するエフェクト。', 'いつでも使用できる。シーンに登場している非オーヴァードのキャラクターは全員エキストラとなる。逆に登場しているオーヴァードは使用されたことが自動的に分かるものとする。このエフェクトの効果は、シーン中持続する。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-コンセントレイト', '基本1', '一般', '一般', 'コンセントレイト', 'コンセントレイト', '3', 3, 'メジャーアクション', 'シンドローム', '—', '—', '—', '2', '—', NULL, '集中力を発揮するエフェクト。', '組み合わせた判定のクリティカル値を−LVする(下限値7)。取得時に自分の取得しているシンドロームからひとつを選び、《コンセントレイト:サラマンダー》のように記述して、シンドロームごとに別エフェクトとして扱う。また、このエフェクトは選択したシンドロームのエフェクトとして扱うこと。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-アタックボーナス', '基本1', '一般', '一般', 'アタックボーナス', 'アタックホーナス', '10', 10, 'メジャーアクション', '効果参照', '対決', '—', '—', '5', '—', NULL, 'レネゲイドによる破壊ポテンシャルそのものを増強するエフェクト。その破壊力はあなどれない。', 'あらゆる攻撃と組み合わせることができる。このエフェクトを組み合わせた攻撃の攻撃力を+[LV×3]する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-カバーディフェンス', '基本1', '一般', '一般', 'カバーディフェンス', 'カハーティフェンス', '3', 3, 'オートアクション', '—', '自動成功', '自身', '至近', '2', '—', NULL, '身を挺して他者をかばうエフェクト。', 'ダメージロールの直前に使用する。あなたはカバーリングを行なう。このカバーリングによって、あなたは行動済みにならず、あなたが行動済みでも使用できる。このエフェクトは1メインプロセスに1回、1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-クイックダッシュ', '基本1', '一般', '一般', 'クイックダッシュ', 'クイックタッシュ', '3', 3, 'セットアッププロセス', '—', '自動成功', '自身', '至近', '4', '—', NULL, '反射をレネゲイドによって加速し、瞬発的な移動を行なうエフェクト。', 'このエフェクトを使用すると、あなたは即座に戦闘移動を行なうことができる。このエフェクトは1シナリオにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ショックアブソーブ', '基本1', '一般', '一般', 'ショックアブソーブ', 'ショックアフソーフ', '10', 10, 'オートアクション', '—', '自動成功', '自身', '至近', '7', '—', NULL, '受ける負傷に対応して、レネゲイドを瞬間的に活性化させるエフェクト。', 'あなたに対するHPダメージを求めるタイミングで使用する。あなたが受ける(予定の)HPダメージを−[LV×5]する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-スピードアップ', '基本1', '一般', '一般', 'スピードアップ', 'スヒートアッフ', '10', 10, '常時', '—', '自動成功', '自身', '至近', '—', '—', NULL, '生存のためより素早く体が反応することを表わすエフェクト。', 'あなたの行動値を+LVする。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-ハイパータフネス', '基本1', '一般', '一般', 'ハイパータフネス', 'ハイハータフネス', '10', 10, '常時', '—', '自動成功', '自身', '至近', '—', '—', NULL, 'あなたのレネゲイドが、非常に高い生命力を与えてくれることを表わすエフェクト。', 'あなたの最大HPを+[LV×3]する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-リフレックス', '基本1', '一般', '一般', 'リフレックス', 'リフレックス', '3', 3, 'リアクション', 'シンドローム', '—', '自身', '至近', '2', '—', NULL, '反射能力を発揮するエフェクト。', '組み合わせた判定のクリティカル値を−LVする(下限値7)。取得時に自分の取得しているシンドロームからひとつを選び、《リフレックス:サラマンダー》のように記述して、シンドロームごとに別エフェクトとして扱う。また、このエフェクトは選択したシンドロームのエフェクトとして扱うこと。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-レストレインコマンド', '基本1', '一般', '一般', 'レストレインコマンド', 'レストレインコマント', '3', 3, 'マイナーアクション', '—', '自動成功', '自身', '至近', '4', '—', NULL, '暴走している自身のレネゲイドを強引に落ち着かせるエフェクト。', 'あなたが受けている、暴走を含むバッドステータスを3個まで回復する。このエフェクトは1シーンにLV回まで使用できる。', 'EA', 0);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-イベイジョン', '基本1', '一般', 'エネミー', 'イベイジョン', 'イヘイション', '1', 1, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーが常に安定した回避能力を発揮することを表わすエフェクト。', 'このエネミーが行なうドッジの達成値を、[ドッジ判定のダイス数×2+使用した技能のレベル]に固定する。ただし、このエネミーはドッジの判定でダイスロールを行なうことができない。『ルール1』P324も参照のこと。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-加速する刻', '基本1', '一般', 'エネミー', '加速する刻', 'カソクスルトキ', '5', 5, 'イニシアチブプロセス', '—', '自動成功', '自身', '至近', '—', '—', NULL, '高速行動するエフェクト。', 'あなたはイニシアチブプロセスでメインプロセスを行なえる。このメインプロセスは行動済みでも行なえ、行なっても行動済みにならない。このエフェクトは侵蝕率によるレベルアップの効果を受けず、1ラウンドに1回、1シナリオにLV回まで使用できる。このエフェクトは他のエフェクトと組み合わせて使用できない。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-献身の盾', '基本1', '一般', 'エネミー', '献身の盾', 'ケンシンノタテ', '1', 1, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーがトループであるにもかかわらず、カバーリングを行なえることを表わすエフェクト。', 'このエフェクトはトループのみが取得できる。このエネミーは、カバーリングを行なうことが可能となる。ただし、カバーリングを行なうにはメジャーアクションを消費すること。『ルール1』P324も参照のこと。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-高速移動', '基本1', '一般', 'エネミー', '高速移動', 'コウソクイトウ', '10', 10, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーが非常に優れた移動能力を持つことを表わすエフェクト。', 'このエネミーが戦闘移動する際の距離を+[LV×5]mする。これによって全力移動の移動距離も変化することに注意。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-瞬間退場', '基本1', '一般', 'エネミー', '瞬間退場', 'シュンカンタイショウ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '—', '—', NULL, '空を飛ぶ、裏道に逃げる、姿を隠すなどして、その場から逃げるエフェクト。', 'いつでも使用できる。このエフェクトを使用することで、このエネミーはシーンから退場する。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-状態復元', '基本1', '一般', 'エネミー', '状態復元', 'ショウタイフクケン', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '—', '—', NULL, '自分が受けている不利な状態をリセットするエネミー用エフェクト。', 'このエネミーがバッドステータスを受けた直後に使用する。そのバッドステータスをすべて打ち消す。ただし、バッドステータスひとつごとにこのエネミーは5点のHPを失う。このエフェクトは重圧を受けていても使用できる。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-生命増強', '基本1', '一般', 'エネミー', '生命増強', 'セイメイソウキョウ', '10', 10, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーがとてつもない耐久力を持つことを表わすエフェクト。その強固さは鉄壁のごとく、いかなる攻撃であろうとこれを突き崩すのは容易ではない。', 'このエネミーの最大HPを+[LV×30]する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-装甲貫通', '基本1', '一般', 'エネミー', '装甲貫通', 'ソウコウカンツウ', '5', 5, 'オートアクション', '—', '自動成功', '自身', '至近', '—', '—', NULL, NULL, 'このエネミーが行なうダメージロールの直前に宣言する。このダメージロールでは、対象の装甲値を無視してダメージを算出する。ただし、このエフェクトを使用したメインプロセスの終了時に、このエネミーはHPを1D失う。このエフェクトは侵蝕率によるレベルアップの効果を受けず、1シーンにLV回まで使用できる。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-蘇生復活', '基本1', '一般', 'エネミー', '蘇生復活', 'ソセイフッカツ', '1', 1, 'オートアクション', '—', '自動成功', '自身', '至近', '—', '—', NULL, 'このエネミーが、力尽きたとしてももう一度立ち上がってくる能力を持つことを表わすエフェクト。', 'このエネミーが戦闘不能、死亡となった時に使用する。戦闘不能、死亡を回復し、このエネミーのHPを1点まで回復させる。このエフェクトは1シナリオに1回まで使用できる。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-反射適応', '基本1', '一般', 'エネミー', '反射適応', 'ハンシャテキオウ', '10', 10, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーが恐るべき素早さを持つ存在であることを表わすエフェクト。そのスピードは常人をはるかに凌駕し、風すらも追いつくことはできない。', 'このエネミーの【行動値】を+[LV×2]する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 53);
INSERT INTO Power (id, supplement, category, type, name, ruby, maxlv, maxlv_int, timing, skill, dfclty, target, rng, encroach, restrict, premise, flavor, effect, update_supplement, type_restrict_order) VALUES ('基本1-予備調達', '基本1', '一般', 'エネミー', '予備調達', 'ヨヒチョウタツ', '10', 10, '常時', '—', '—', '自身', '至近', '—', '—', NULL, 'このエネミーがより多くの装備類を調達し、多くの武器や資金などを持つことを表わすエフェクト。', 'このエネミーの常備化ポイントを、+[LV×10]する。このエフェクトは侵蝕率によるレベルアップの効果を受けない。', 'EA', 53);
